// import './Favorites.css'

export default function Favorites({favorites, handleRemoveFavorite}) {
  return (
    <div className="favorites-container">
        <h2>Favorites</h2>
        {/* Map over favorites and render each item */}
        {favorites.map((favorite, index) => (
          <div className='favorite' key={index}>
            <img src={favorite.artworkUrl100} alt={`${favorite.trackName} artwork`} />
            <p>{favorite.artistName} - {favorite.trackName}</p>
            <p></p>
            <p>{favorite.artistViewUrl}</p>
            <button onClick={() => handleRemoveFavorite(favorite)}>Remove</button>
          </div>
        ))}
      </div>
  )
}
