import React from 'react';

const Results = ({ searchResults, handleFavorite }) => {
  return (
    <div className="results-container">
      {searchResults.length === 0 ? (
        <p>No matches found.</p> // display "No matches found" message when there are no search results
      ) : (
        <ul>
          {searchResults.map((result) => (
            <li key={result.trackId}>
              <img src={result.artworkUrl60} alt={result.trackName} />
              <span>{result.trackName}</span>
              <button onClick={() => handleFavorite(result)}>Favorite</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Results;
